import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';


const useStyles = makeStyles(theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
    margin: 'auto'
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
  button: {
    margin: theme.spacing(1),
  },
  rightIcon: {
    marginLeft: theme.spacing(1),
  }
}));


export default function TextFieldMargins(props) {
  const classes = useStyles();

  const [name, setName] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [message, setMessage] = React.useState("");

  function handleSubmit(e) {
    e.preventDefault();
    console.log("Data submitted!", {name, email, message})

    fetch('http://localhost:80/posts', {
  method: 'POST',
  headers: {
    Accept: 'application/json',
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({
    "name": {name},
    "email": {email},
    "message": {message},
  }),
});console.log(JSON.parse())
  }

  return (
    <div className={classes.container}>
      <TextField   
        variant="outlined"
        label="Name"
        id="Name_field"
        placeholder="Please enter your name"
        className={classes.textField}
        helperText="Help us get to know you!"
        margin="normal"
        value={name}
        onChange={e => setName(e.target.value)}
      />
      <TextField
        variant="outlined"
        label="Email"
        id="Email_field"
        placeholder="Valid Email"
        className={classes.textField}
        helperText="How can we reach you?"
        margin="normal"
        value={email}
        onChange={e => setEmail(e.target.value)}
      />
      <TextField
        variant="outlined"
        label="Message"
        id="Message_field"
        placeholder="Message"
        className={classes.textField}
        helperText="What can we help you with?"
        margin="normal"
        value={message}
        onChange={e => setMessage(e.target.value)}
      />
      <Button onClick={handleSubmit} variant="contained" color="primary" className={classes.button}>
        Send
        <Icon className={classes.rightIcon}>send</Icon>
      </Button>
    </div>
    
  );
}
